package com.android.studyimport android.content.BroadcastReceiverimport android.content.Contextimport android.content.Intentimport android.util.Logclass MyBroadcastReceiver : BroadcastReceiver() {    override fun onReceive(context: Context?, intent: Intent?) {        try {            Log.d(TAG, "onReceive: $intent")            if (intent?.action == "android.intent.tec.action.sendotherapp") {                val detailsString = intent.getStringExtra("details_string")                Log.i(TAG, "Received broadcast with details: $detailsString")                // 在这里处理接收到的数据            }        } catch (e: Exception) {            e.printStackTrace()        }    }    companion object {        private const val TAG = "MyBroadcastReceiver"    }}